var CurrentPlaying = {
	get song() {
		return document.querySelector('#menu-song_playing-song_name').innerHTML;
	},
	set song(val) {
		document.querySelector('#menu-song_playing-song_name').innerHTML = val;
	},
	get artist() {
		return document.querySelector('#menu-song_playing-artist_name').innerHTML;
	},
	set artist(val) {
		document.querySelector('#menu-song_playing-artist_name').innerHTML = val;
	},
	set coverImage(val) {
		document.querySelector('#menu-song_playing-album_cover').src  = val;
	},
	get coverImage() {
		return document.querySelector('#menu-song_playing-album_cover').src;
	},
	clearCoverImage: function() {
		document.querySelector('#menu-song_playing-album_cover').src = 'data:image/png;base64,';
	},
	clear: function() {
		document.querySelector('#menu-song_playing-album_cover').src = 'data:image/png;base64,';
		document.querySelector('#menu-song_playing-song_name').innerHTML = 'Nothing playing'
		document.querySelector('#menu-song_playing-artist_name').innerHTML = 'Search for something';
	}
};
var PlayPauseButton = new (function() {
	var self = this;

	this.el = document.querySelector('#player-controller_buttons');
	this.i = document.querySelector('#player-controller_buttons-play_pause');
	this.backward = document.querySelector('#player-controller_buttons-backward');
	this.forward = document.querySelector('#player-controller_buttons-forward');

	this.play = function() {
		if(YouTube.player.playVideo) {
			this.el.classList.remove('play');
			this.el.classList.add('pause');
			this.i.classList.remove('fa-play');
			this.i.classList.add('fa-pause');
			YouTube.player.playVideo();
		}
	};
	this.pause = function(pauseAnyway) {
		if(YouTube.player.pauseVideo || pauseAnyway) {
			this.el.classList.remove('pause');
			this.el.classList.add('play');
			this.i.classList.remove('fa-pause');
			this.i.classList.add('fa-play');
			if(YouTube.player.pauseVideo) YouTube.player.pauseVideo();
		}
	}

	this.i.addEventListener('click', function(ev) {
		if(self.el.classList.contains('play')) {
			self.play();
		} else {
			self.pause();
		}
	})
	this.backward.addEventListener('click', function(ev) {
		PlayQueue.currentPosition--;
		PlayQueue.highlightPlayingSong()
	})
	this.forward.addEventListener('click', function(ev) {
		PlayQueue.currentPosition++;
		PlayQueue.highlightPlayingSong()
	})
});

document.body.addEventListener('mouseover', function(ev) {
	if(ev.target.classList.contains('album')) {
		ev.target.classList.toggle('album-play');
	}
});
document.body.addEventListener('mouseout', function(ev) {
	if(ev.target.classList.contains('album')) {
		ev.target.classList.toggle('album-play')
		var i = ev.target.querySelector('i');
	}
});

function Slider(el, sliderChangeValue) {
	var self = this;

	this.sliderChangeValue = sliderChangeValue;

	this.el = {
		slider: el,
		drag: el.querySelector('.slider-drag'),
		completed: el.querySelector('.slider-completed'),
		full: el.querySelector('.slider-full'),
		songTotal: el.querySelector('.slider-song_total'),
		songCompleted: el.querySelector('.slider-song_completed')
	};
	this.clear = function() {
		self.el.drag.style.left = 0;
		self.el.completed.style.width = 0;
		self.songLength.completed = 0;
		self.songLength._.total = 0;
		self.el.songTotal.innerHTML = '--:--';
		self.el.songCompleted.innerHTML = '--:--';
	}
	this.coords = {};
	this.completed = {
		get px() {
			return self.coords.x - Math.round(self.el.completed.getBoundingClientRect().left);
		},
		get percent() {
			return self.el.completed.clientWidth / self.el.full.clientWidth * 100;
		},
		set percent(val) {
			self.el.drag.style.left = volume.el.full.clientWidth * val/100 + 'px';
			self.el.completed.style.width = volume.el.full.clientWidth * val/100 + 'px';
		}
	};
	this.songLength = {
		_: {},
		get total() {
			return self.songLength._.total;
		},
		set total(val) {
			if(!self.el.songCompleted || !self.el.songTotal) return;

			var timeStr = Math.floor(val / 60) + ':';

			if(val%60 < 10) timeStr += '0';

			timeStr += val%60;

			self.songLength._.total = val;
			self.el.songTotal.innerHTML = timeStr;
		},
		get completed() {
			return Math.round((self.songLength.total * self.completed.percent) / 100);
		},
		set completed(val) {
			if(!self.el.songCompleted || !self.el.songTotal) return;

			var percent = val / self.songLength.total;
			var totalPx = self.el.full.clientWidth;
			var timeStr = Math.floor(val / 60) + ':';

			if(val%60 < 10) timeStr += '0';
			timeStr += val%60;

			self.el.songCompleted.innerHTML = timeStr;

			self.el.drag.style.left = Math.round(totalPx * percent) + 'px';
			self.el.completed.style.width = Math.round(totalPx * percent) + 'px';
		},
		updateCompletedString: function() {
			if(!self.el.songCompleted || !self.el.songTotal) return;

			var percent = self.completed.px / self.el.full.clientWidth;
			var totalLength = self.songLength.total;
			var completedLength = Math.round(percent * totalLength);

			var timeStr = Math.floor(completedLength / 60) + ':';

			if(completedLength%60 < 10) timeStr += '0';
			timeStr += completedLength%60;

			self.el.songCompleted.innerHTML = timeStr;
		}
	};
	this.dragging = false;

	self.el.drag.addEventListener('mousedown', function(ev) {
		self.dragging = true;
		self.el.drag.classList.add('slider-drag_focus');
	})
	self.el.drag.addEventListener('dragstart', function(ev) {
		ev.preventDefault();
	})
	document.body.addEventListener('mouseup', function() {
		if(self.dragging && self.sliderChangeValue) {
			self.sliderChangeValue(self.completed.percent, self.songLength.completed)
		}
		self.dragging = false;
		self.el.drag.classList.remove('slider-drag_focus');
	})
	document.body.addEventListener('mousemove', function(ev) {
		self.coords.x = ev.clientX;
		self.coords.y = ev.clientY;

		var completedPx = self.completed.px;
		var completedPercent = self.completed.percent;

		if(self.dragging && completedPx >= 0 && completedPx <= self.el.full.clientWidth) {
			self.el.drag.style.left = completedPx + 'px';
			self.el.completed.style.width = completedPx + 'px';

			self.songLength.updateCompletedString();
		}
	})
	self.el.full.addEventListener('mousedown', function(ev) {
		var completedPx = self.completed.px;
		var completedPercent = self.completed.percent;

		if(completedPx >= 0 && completedPx <= self.el.full.clientWidth) {
			self.el.drag.style.left = completedPx + 'px';
			self.el.completed.style.width = completedPx + 'px';
			self.songLength.updateCompletedString();
		}
		if(self.sliderChangeValue) {
			self.sliderChangeValue(self.completed.percent, self.songLength.completed)
		}
	})
};

var slider = new Slider(document.querySelector('#player-slider'), function(percent, song) {
	if(YouTube.player && YouTube.player.seekTo) {
		YouTube.player.seekTo(song);

	} 
})
var volume = new Slider(document.querySelector('#player-volume-slider'), function(percent) {
	if(YouTube.player && YouTube.player.setVolume) {
		YouTube.player.setVolume(Math.round(percent));
	}
});
volume.completed.percent = 50;

document.querySelector('#player-volume-slider i').addEventListener('click', function(ev) {
	if(ev.target.classList.contains('fa-volume-up')) {
		ev.target.setAttribute('class', 'fa fa-fw fa-volume-down')
	}
	if(ev.target.classList.contains('fa-volume-down')) {
		ev.target.classList.add('fa-volume-up');
		ev.target.classList.remove('fa-volume-down');
	}
});